  <script type="text/javascript">
		var manual_login = document.getElementById("manual_login");
		var login_button = document.getElementById("login_button");
    
    <?php if($captive) { ?>
    var cpa_request = function() {
      var ua = window.navigator.userAgent.toLowerCase();
  
      return (ua.indexOf("iphone") !== -1 || ua.indexOf("ipad") !== -1) &&
              ua.indexOf("safari/") == -1;
    }
	
    if (cpa_request()) {
      setTimeout(function() {
        var click_to_button = function(node){
          if ( document.createEvent ) {
            var evt = document.createEvent("MouseEvents");
            evt.initEvent("click", true, false);
            node.dispatchEvent(evt);  
          } else if( document.createEventObject ) {
            node.fireEvent("onclick"); 
          } else if (typeof node.onclick == 'function' ) {
            node.onclick(); 
          }
        }

        login_button.onclick = function(){ 
          login_button.click(); 
        };
        click_to_button(login_button);
      }, 1500);
    } else {
      setTimeout(function() {
        login_button.click(); 
      }, 100);
    }
    
    <?php } else { ?>
    setTimeout(function() {
      login_button.click(); 
    }, 100);
    <?php } ?>

    manual_login.style.display = "none";
    setTimeout(function() {
      manual_login.style.display = "block";
    }, 5000);

  </script>
